const mapCatToId = (cat) => {
  switch (cat) {
    case "hats":
      return 1;
    case "sneakers":
      return 2;
    case "jackets":
      return 3;
    case "womens":
      return 4;
    case "mens":
      return 5;
    default:
      return -1;
  }
};

const getSpecificCat = async (allCat, catToFind) => {
  const catToFindId = await mapCatToId(catToFind);
  console.log(catToFindId);

  await allCat.find((cat) => cat.id === catToFindId);
};

export default getSpecificCat;
