const mapCatToId = (cat) => {
  switch (cat) {
    case "hats":
      return 1;
    case "sneakers":
      return 1;
    case "jackets":
      return 1;
    case "womens":
      return 1;
    case "mens":
      return 1;
    default:
      return -1;
  }
};

const getSpecificCat = (catToFind) => {
  const catToFindId = mapCatToId(catToFind);
  console.log(catToFindId);
};

export default getSpecificCat;
