import { createGlobalStyle } from "styled-components";

export const GlobalStyles = createGlobalStyle`
  * {
    font-family: "Open Sans Condensed", sans-serif;
    box-sizing: border-box;
    
  }

  body {
    padding: 20px 60px;
  }

  a {
    text-decoration: none;
    color: black;
  }
`;
