export const helper = (allItems, newItem) => {
  var found = allItems.find((item) => item.id === newItem.id);
  if(found){
    return allItems.map(item=>{
      item.id===newItem.id?{...allItems, quantity:item.quantity+1}:item
    })
  }
  return [...allItems,{newItem,}] 
};
