import CartActionTypes from './cart.types'

export const addItemToCart = (cartItems, cartItemToAdd) => {
  const existingCartItem = cartItems.find(
    (cartItem) => cartItem.id === cartItemToAdd.id
  );

  if (existingCartItem) {
    return cartItems.map((cartItem) =>
      cartItem.id === cartItemToAdd.id
        ? { ...cartItem, quantity: cartItem.quantity + 1 }
        : cartItem
    );
  }

  return [...cartItems, { ...cartItemToAdd, quantity: 1 }];
};

export const totalPriceCal = (cartItem, cartItemToAdd,action) => {
  let price = 0;
  for (let key in cartItem) {
    price += cartItem[key].quantity * cartItem[key].price;
  }
  if(action.type===CartActionTypes.ADD_ITEM)
    price += cartItemToAdd.price;
  if(action.type===CartActionTypes.CLEAR_ITEM_FROM_CART)
    price -=action.payload.price
  return price;
};
